function kset(key,value){window.localStorage.setItem(key,value);}
function kget(key){return window.localStorage.getItem(key);}
function kremove(key){window.localStorage.removeItem(key);}
function kclear(){window.localStorage.clear();}
function kupdate(key,value){window.localStorage.removeItem(key);window.localStorage.setItem(key,value);}
function getUrlParam(string){var obj={};if(string.indexOf("?")!=-1){var string=string.substr(string.indexOf("?")+1);var strs=string.split("&");for(var i=0;i<strs.length;i++){var tempArr=strs[i].split("=");obj[tempArr[0]]=unescape(tempArr[1]);}}
return obj;}